$(document).ready(function() {
    $('.select').select2({
      minimumResultsForSearch: Infinity
    });
    $('.select_filter-city').select2({
      minimumResultsForSearch: Infinity,
      placeholder: "Город"
    });
    $('.select_filter-time').select2({
      minimumResultsForSearch: Infinity
    });

});

let searchBtn = document.querySelector(".search__btn");
let searchInput = document.querySelector(".search__input");

searchBtn.addEventListener("click", function() {
  searchInput.classList.toggle("active");
})

var range = document.querySelector(".range");

noUiSlider.create(range, {
  range: {
    min: 0,
    max: 30000
  },
  start: [0, 30000],
  step: 100,

  // Display colored bars between handles
  connect: true,

  // Move handle on tap, bars are draggable
  behaviour: "tap-drag",
  tooltips: true,
  format: {
    to: function(value) {
      return Math.floor(value) + " ₽";
    },
    from: function(value) {
      return Math.floor(value);
    }
  }
});

function openTab(evt, name) {
  var i, tabcontent, tablinks;

  tabcontent = document.getElementsByClassName("tabs__content");
  for (i = 0; i < tabcontent.length; i++) {
    tabcontent[i].style.display = "none";
  }

  tablinks = document.getElementsByClassName("tabs__tab");
  for (i = 0; i < tablinks.length; i++) {
    tablinks[i].className = tablinks[i].className.replace(" active", "");
  }

  document.getElementById(name).style.display = "block";
  evt.currentTarget.className += " active";
}

var defaultClick = document.querySelector("._default");

if (defaultClick) {
  defaultClick.click();
}
